mapper.select:
select ord.order_id, task.task_id, task.task_name, task.amount, ord.created_at, ord.updated_at from t_task task left join t_order ord on ord.task_id = task.task_id left join t_user `user` on `user`.open_id = ord.open_id
ord.open_id = #{openId} and ord.status = #{status} and `user`.is_white_list = 1 and ord.is_deleted = 0
limit #{offset,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}
